import logo from './logo.svg';
import './App.css';
import { WsProvider, ApiPromise } from "@polkadot/api";
import { useEffect, useState } from "react";

function App() {

const [api, setApi] = useState<ApiPromise | undefined>(undefined);
const setup = async () => {
	const wsProvider = new WsProvider("wss://westend-rpc.polkadot.io");
	const api = await ApiPromise.create({provider : wsProvider});
	setApi(api);
}

useEffect(() => {
	setup();
}, []);

useEffect(() => {
	if (!api) return;
	(async () => {
		const time = await api.query.timestamp.now();
		console.log(time.toPrimite());
	})();

}, [api]);



return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white">
      <header className="text-center">
        <img src={logo} className="h-40 pointer-events-none" alt="logo" />
        <h1 className="text-4xl font-bold mt-6">Welcome to PookieMath</h1>
        <p className="text-lg mt-4">
          PookieMath is a powerful tool for practicing arithmetic operations. Start practicing now to improve your skills!
        </p>
        <button
          className="mt-6 px-6 py-3 text-lg font-semibold text-gray-900 bg-blue-400 rounded-lg hover:bg-blue-300"
          onClick={() => {
            // Navigate to the main application or start the practice session
            // For example, you can use React Router to navigate to a different route
          }}
        >
          Start Practicing
        </button>
        <a
          className="block mt-4 text-blue-400 hover:underline"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
