import React, { useState, useEffect } from 'react';
import { ThinClient } from '@unique-nft/sdk';
import { Web3AccountsOptions } from '@polkadot/extension-inject/types';
import { PolkadotProvider } from '@unique-nft/accounts/polkadot';

const Poookie = () => {
  const [firstNumber, setFirstNumber] = useState(0);
  const [secondNumber, setSecondNumber] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [sdk, setSdk] = useState(null);
  const [accountInfo, setAccountInfo] = useState(null);
  const [accountLoaded, setAccountLoaded] = useState(false);

  useEffect(() => {
    const setupAccount = async () => {
      const options = { accountType: ['sr25519'] };
      const provider = new PolkadotProvider(options);
      await provider.init();

      const account = await provider.first();
      const sdkInstance = new ThinClient({
        baseUrl: 'https://rest.unique.network/opal/v1',
        account,
      });
      setSdk(sdkInstance);

      const address = account.address;
      const balance = await sdkInstance.balance.get({ address });
      setAccountInfo({ address, balance });
      setAccountLoaded(true);
    };

    setupAccount();
  }, []);

  const generateNewQuestion = () => {
    const num1 = Math.floor(Math.random() * 10);
    const num2 = Math.floor(Math.random() * 10);
    setFirstNumber(num1);
    setSecondNumber(num2);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const correctAnswer = firstNumber + secondNumber;
    if (parseInt(userAnswer) === correctAnswer) {
      setScore(score + 1);
      setFeedback('Correct!');

      if ((score + 1) % 5 === 0) {
        try {
          const collectionName = 'PookieMath Collection';
          const tokenPrefix = 'PMT';

          let collectionId;
          const collections = await sdk.collection.list();
          const existingCollection = collections.data.find(
            (c) => c.name === collectionName && c.tokenPrefix === tokenPrefix
          );

          if (existingCollection) {
            collectionId = existingCollection.collectionId;
          } else {
            const { parsed, error } = await sdk.collection.create.submitWaitResult({
              name: collectionName,
              description: 'Collection for PookieMath rewards',
              tokenPrefix,
            });

            if (error) throw new Error('Error occurred while creating a collection');
            if (!parsed) throw new Error('Cannot parse results');

            collectionId = parsed.collectionId;
          }

          const createTokenResult = await sdk.token.create({
            collectionId,
            properties: [
              { key: 'name', value: 'Reward NFT' },
              { key: 'description', value: 'A reward for achieving 5 correct answers!' },
            ],
          });

          if (!createTokenResult) throw new Error('Failed to create token');

          const { tokenId } = createTokenResult;

          await sdk.token.transfer({
            collectionId,
            tokenId,
            to: accountInfo.address,
          });

          setFeedback('Correct! You earned an NFT!');
        } catch (error) {
          console.error('Failed to mint and transfer NFT:', error);
          setFeedback('Correct! But there was an error minting the NFT.');
        }
      }
    } else {
      setFeedback(`Incorrect! The correct answer was ${correctAnswer}`);
    }
    setUserAnswer('');
    generateNewQuestion();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-red-50">
      <div className="bg-red-100 shadow-lg rounded-lg p-8">
        <h1 className="text-3xl font-bold text-red-700 mb-4">PookieMath</h1>
        {!accountLoaded ? (
          <p>Loading account...</p>
        ) : (
          <>
            <div className="flex justify-between">
              <div>
                <div className="text-2xl mb-4">
                  <span>{firstNumber}</span> + <span>{secondNumber}</span>
                </div>
                <form onSubmit={handleSubmit} className="flex flex-col items-center">
                  <input
                    type="number"
                    value={userAnswer}
                    onChange={(e) => setUserAnswer(e.target.value)}
                    className="p-2 border border-red-300 rounded mb-4 focus:outline-none focus:ring-2 focus:ring-red-500"
                    placeholder="Your answer"
                  />
                  <button
                    type="submit"
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                  >
                    Submit
                  </button>
                </form>
                <div className="mt-4">
                  <p className="text-xl">{feedback}</p>
                  <p className="text-2xl">Score: {score}</p>
                </div>
              </div>
              {accountInfo && (
                <div className="ml-8 p-4 border border-red-300 rounded bg-red-50">
                  <h2 className="text-xl font-bold">Account Information</h2>
                  <p><strong>Address:</strong> {accountInfo.address}</p>
                  <p><strong>Balance:</strong> {accountInfo.balance?.free} UQs</p>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Poookie;
