import React, { useEffect, useState } from 'react';
import { formatBalance } from "@polkadot/ui-util";
import { web3FromAddress } from '@polkadot/extension-dapp';
import BN from "bn.js";

const AMOUNT = new BN(10).mul(new BN(10).pow(new BN(12)));

const ZetamacGame = ({ api, account }) => {
  const [problem, setProblem] = useState(generateProblem());
  const [input, setInput] = useState('');
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(60);
  const [isGameOver, setIsGameOver] = useState(false);
  const [balance, setBalance] = useState(null);

  const generateProblem = () => {
    const num1 = Math.floor(Math.random() * 10);
    const num2 = Math.floor(Math.random() * 10);
    const operator = Math.random() > 0.5 ? '+' : '-';
    const answer = operator === '+' ? num1 + num2 : num1 - num2;
    return { num1, num2, operator, answer };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (parseInt(input) === problem.answer) {
      setScore(score + 1);
      setProblem(generateProblem());
    }
    setInput('');
  };

  const handleBurn = async () => {
    if (!api || !account) return;

    const injector = await web3FromAddress(account.address);

    await api.tx.currencies.burnFren(AMOUNT).signAndSend(account.address, {
      signer: injector.signer,
    });
  };

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setInterval(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearInterval(timer);
    } else {
      setIsGameOver(true);
    }
  }, [timeLeft]);

  useEffect(() => {
    if (!api || !account) return;

    api.query.system.account(account.address, ({ data: { free } }) => {
      setBalance(free);
    });
  }, [api, account]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full text-center">
        <h1 className="text-2xl font-bold mb-4">Zetamac Game</h1>
        {isGameOver ? (
          <div>
            <h2 className="text-xl font-bold">Game Over!</h2>
            <p className="text-lg">Your score: {score}</p>
            <button
              onClick={() => {
                setScore(0);
                setTimeLeft(60);
                setIsGameOver(false);
                setProblem(generateProblem());
              }}
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md"
            >
              Play Again
            </button>
          </div>
        ) : (
          <div>
            <div className="text-lg mb-4">
              <span>{problem.num1}</span> <span>{problem.operator}</span> <span>{problem.num2}</span>
            </div>
            <form onSubmit={handleSubmit}>
              <input
                type="number"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                className="border rounded px-2 py-1 mb-4 text-center w-full"
              />
              <button
                type="submit"
                className="px-4 py-2 bg-blue-500 text-white rounded-md w-full"
              >
                Submit
              </button>
            </form>
            <div className="mt-4">
              <p className="text-lg">Score: {score}</p>
              <p className="text-lg">Time left: {timeLeft}s</p>
            </div>
            <div className="mt-4">
              <span>BALANCE: {formatBalance(balance, { withUnit: "FREN" })}</span>
              <button
                onClick={handleBurn}
                className="mt-4 px-4 py-2 bg-red-500 text-white rounded-md"
              >
                BURN 10 $FREN
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ZetamacGame;
