import React, { useState, useEffect } from 'react';
import { WsProvider, ApiPromise } from "@polkadot/api";
import { web3Accounts, web3Enable, web3FromAddress } from "@polkadot/extension-dapp";
import { formatBalance } from "@polkadot/ui-util";
import BN from "bn.js";

const NAME = "PookeyMath";
const AMOUNT = new BN(10).mul(new BN(10).pow(new BN(12)));

const Login = ({ onLogin }) => {
  const [api, setApi] = useState(null);
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [period, setPeriod] = useState(null);
  const [balance, setBalance] = useState(null);

  const setup = async () => {
    const wsProvider = new WsProvider("wss://ws.gm.bldnodes.org/");
    const apiInstance = await ApiPromise.create({ provider: wsProvider });
    setApi(apiInstance);
  };

  const handleConnection = async () => {
    const extensions = await web3Enable(NAME);

    if (!extensions) {
      throw Error("NO_EXTENSION_FOUND");
    }

    const allAccounts = await web3Accounts();
    setAccounts(allAccounts);

    if (allAccounts.length === 1) {
      setSelectedAccount(allAccounts[0]);
      onLogin(api, allAccounts[0]);
    }
  };

  const handleAccountSelection = async (e) => {
    const selectedAddress = e.target.value;

    const account = accounts.find(account => account.address === selectedAddress);

    if (!account) {
      throw Error("NO_ACCOUNT_FOUND");
    }

    setSelectedAccount(account);
    onLogin(api, account);
  };

  useEffect(() => {
    setup();
  }, []);

  return (
    <div className="bg-neutral-100 flex items-center justify-center h-[100svh]">
      {accounts.length === 0 && (
        <button onClick={handleConnection} className="rounded-md px-4 py-2 bg-neutral-800 text-white">
          Connect
        </button>
      )}

      {accounts.length > 0 && !selectedAccount && (
        <select onChange={handleAccountSelection} className="rounded-md">
          <option value="" disabled selected hidden key="nothing">Choose your account</option>
          {accounts.map(account => (
            <option value={account.address} key={account.address}>
              {account.meta.name || account.address}
            </option>
          ))}
        </select>
      )}
    </div>
  );
};

export default Login;
