import React, { useState, useEffect } from 'react';
import { Sdk } from '@unique-nft/sdk'; 
import { Account } from '@unique-nft/accounts';
import { Sr25519Account } from "@unique-nft/sr25519";
const mnemonic = "<SET THE MNEMONIC SEED PHRASE FOR THE DEFAULT SIGNER>";
const account = Sr25519Account.fromUri(mnemonic);

const Poookie = () => {
  const [firstNumber, setFirstNumber] = useState(0);
  const [secondNumber, setSecondNumber] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [sdk, setSdk] = useState(null);

  useEffect(() => {
    const sdkInstance = new Sdk({
      baseUrl: 'https://rest.unique.network/opal/v1', 
      account,
    });
    setSdk(sdkInstance);

    generateNewQuestion();
  }, []);

  const generateNewQuestion = () => {
    const num1 = Math.floor(Math.random() * 10);
    const num2 = Math.floor(Math.random() * 10);
    setFirstNumber(num1);
    setSecondNumber(num2);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const correctAnswer = firstNumber + secondNumber;
    if (parseInt(userAnswer) === correctAnswer) {
      setScore(score + 1);
      setFeedback('Correct!');
    } else {
      setFeedback(`Incorrect! The correct answer was ${correctAnswer}`);
    }
    setUserAnswer('');
    generateNewQuestion();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-red-50">
      <div className="bg-red-100 shadow-lg rounded-lg p-8">
        <h1 className="text-3xl font-bold text-red-700 mb-4">PookieMath</h1>
        <div className="text-2xl mb-4">
          <span>{firstNumber}</span> + <span>{secondNumber}</span>
        </div>
        <form onSubmit={handleSubmit} className="flex flex-col items-center">
          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            className="p-2 border border-red-300 rounded mb-4 focus:outline-none focus:ring-2 focus:ring-red-500"
            placeholder="Your answer"
          />
          <button
            type="submit"
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Submit
          </button>
        </form>
        <div className="mt-4">
          <p className="text-xl">{feedback}</p>
          <p className="text-2xl">Score: {score}</p>
        </div>
      </div>
    </div>
  );
};

export default Poookie;
