import React, { useEffect, useState } from 'react';
import { ApiPromise, WsProvider } from '@polkadot/api';
import { web3Accounts, web3Enable } from '@polkadot/extension-dapp';
//import { error } from 'console';

const NAME = "PookeyMath";
const MyComponent = () => {
  const [api, setApi] = useState();
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState();


  const setup = async () => {
    const wsProvider = new WsProvider("wss://ws.gm.bldnodes.org/");
	//wss://westend-rpc.polkadot.io
    const api = await ApiPromise.create({ provider: wsProvider });
    setApi(api);
  };

  const handleConnection = async () => {
	const extensions = await web3Enable(NAME);

	if (!extensions) {
		console.log("error no extension found!");
	}

	const allAccounts = await web3Accounts();

	console.log(allAccounts);
	setAccounts(allAccounts);

	if (allAccounts.length == 1) {
		setSelectedAccount(allAccounts[0]);
	}
  }

  useEffect(() => {
    setup();
  }, []);

  useEffect(() => {
    if (!api) return;
    (async () => {
      const time = await api.query.timestamp.now();
      console.log(time.toPrimitive());
    })();
  }, [api]);

  return (
  
	<html>

	<div>My Polkadot API Component</div>
	<div>
		
		{accounts.length == 0 ? (<button onClick={handleConnection}> Connect </button>) : null}
		{selectedAccount ? (
			selectedAccount.address
		) : (

			<>
				<select>
					accounts.map((acount) => (
						<option value={account.address}>{account.address}</option>
					))

				</select>
			
			</>
		)


		}
	</div>
	</html>

  
	);

};

export default MyComponent;
